import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailedScreen = () => {
	const [productList, setProductList] = useState([]);
	const { id } = useParams();

	useEffect(() => {
		axios
			.get(`https://cms.meamacollect.ge/meama-collect/api/client/ka`)
			.then((getData) => {
				setProductList(getData.data);
			});
	}, []);

	const allProducts = productList.map((product) => {
		const filtered = product.products.map((drink) => drink);
		const filteredProducts = filtered.map((single) => single);
		const ready = filteredProducts.filter((el) => el.id === +id);
		console.log(ready[0].console);

		if(ready[0].price) {
			return (
				<div>{ready[0].price}</div>
			)
		}else{
			return (
				<p>error</p>
			)
		}
	});

	const navigate = useNavigate();

	const navigateHandler = () => {
		navigate("/");
	};

	return (
		<div>
			<h1>Detailed Screen</h1>
			{allProducts} 2<button onClick={navigateHandler}>Navigate</button>
		</div>
	);
};

export default DetailedScreen;
