import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailedScreen = () => {
	const [productList, setProductList] = useState([]);
	const { id } = useParams();
	console.log(id)

	useEffect(() => {
		axios
			.get(`https://cms.meamacollect.ge/meama-collect/api/client/ka`)
			.then((getData) => {
				setProductList(getData.data);
			});
	}, []);

	console.log(productList)

	const allProducts = productList.map((product) => {
	const filtered = product.products.map((drink) => drink)
	const arr = [];
	const filteredProducts = filtered.map((single) => arr.push(single))
	const newarr = arr.filter((tea) => console.log(tea.id === +id))
		return (
			<div>{filteredProducts}</div>
		)
	})

	const navigate = useNavigate();

	const navigateHandler = () => {
		navigate("/");
	};

	return (
		<div>
			<h1>Detailed Screen</h1>
             <div>{allProducts}</div>	
			<button onClick={navigateHandler}>Navigate</button>
		</div>
	);
};

export default DetailedScreen;
