import React, { useState, useEffect } from "react";
import axios from "axios";
import classes from "./Products.module.css";
import Category from "./Category";

const Products = () => {
	const [productList, setProductList] = useState([]);

	// useEffect(() => {
	// 	axios
	// 		.get(`https://cms.meamacollect.ge/meama-collect/api/client/ka`)
	// 		.then((getData) => {
	// 			setProductList(getData.data);

	// 			console.log(getData.data);
	// 		});
	// }, []);
	useEffect(() => {
		const fetchProducts = async () => {
			const response = await fetch(
				"https://cms.meamacollect.ge/meama-collect/api/client/ka"
			);
			const data = await response.json();
			const firstFiveProducts = data.slice(10, 5);
			setProductList(firstFiveProducts);
		};

		fetchProducts();
	}, []);

	return (
		<div>
			<Category />
			{productList.map((data) =>
				data.products.map((product) => (
					<div>
						<div style={{ backgroundColor: `${product.bgColor}` }}>
							<p>{product.name}</p>
							<img
								src={product.mainPhoto}
								alt=""
								className={classes.imgS}
							/>
							<h1>{product.price}ლარი</h1>
						</div>
					</div>
				))
			)}
		</div>
	);
};

export default Products;
