import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailedScreen = () => {
	const [productList, setProductList] = useState([]);
	const { id } = useParams();

	useEffect(() => {
		axios
			.get(`https://cms.meamacollect.ge/meama-collect/api/client/ka`)
			.then((getData) => {
				setProductList(getData.data);
			});
	}, []);

	console.log(productList)

	const allProducts = productList.map((product) => {
	const filtered = product.products.map((drink) => drink.id)
	const filteredProducts = filtered.filter((tea) => tea === id)

		return (
			<>
			<h1>{filteredProducts}</h1>
			</>
		)
	})

	const navigate = useNavigate();

	const navigateHandler = () => {
		navigate("/");
	};

	return (
		<div>
			<h1>Detailed Screen</h1>
             {allProducts}	
			{/* <p>{id}</p> */}
			<button onClick={navigateHandler}>Navigate</button>
		</div>
	);
};

export default DetailedScreen;
